package core_java;

public class For_loop {
static int i =0;
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// looping means to continue based on a given condition
		// purpose of this program is to print values from 0 to 29
		// debugging means to execute a program based on debugging points
		
		for
		(int i =0;  // starting value of i (i=0)
			i<30;  // condition
			i+=1)
		{
		
			System.out.println(i);
		}
					
	}

}
