package core_java;

public class If_else {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int u = 80;
		int n = 120;
		int o = 200;

		if (o>n)
		{
		System.out.println("This weight is considered in the obese category");
			}
		else
		{
			System.out.println("This weight is considered healthy");
			}
		if(u<n)
		{
			System.out.println("This weight falls under the underweight category");
		}
		else
		{
			System.out.println("This weight is considered healthy");
		}
			}
}
	


